INSERT INTO [dbo].[GALLERY_ITEM] ([Gallery_Item_Id], [Icon_File_Name_Small], [Icon_File_Name_Large], [Configuration_Setup], [Configuration_Setup_Client], [Description], [Title], [CreationDate], [Is_Visible]) VALUES (83, N'NISTIR_7628_R1.png', N'NISTIR_7628_R1.png', N'{Sets:["NISTIR_7628_R1"],SALLevel:"Low",QuestionMode:"Questions"}', NULL, N'Guidelines for Smart Grid Cybersecurity, presents an analytical framework that organizations can use to develop effective cybersecurity strategies tailored to their particular combinations of smart grid-related characteristics, risks, and vulnerabilities. Organizations in the diverse community of smart grid stakeholdersfrom utilities to providers of energy management services to manufacturers of electric vehicles and charging stationscan use the methods and supporting information presented in this report as guidance for assessing risk and identifying and applying appropriate security requirements. This approach recognizes that the electric grid is changing from a relatively closed system to a complex, highly interconnected environment.', N'NISTIR 7628 Guidelines for Smart Grid Cyber Security: Volume 1 Revision 1', '2022-09-20 15:26:46.847', 1)
INSERT INTO [dbo].[GALLERY_ITEM] ([Gallery_Item_Id], [Icon_File_Name_Small], [Icon_File_Name_Large], [Configuration_Setup], [Configuration_Setup_Client], [Description], [Title], [CreationDate], [Is_Visible]) VALUES (84, N'Nrc_571.png', N'Nrc_571.png', N'{Sets:["Nrc_571"],SALLevel:"Low",QuestionMode:"Questions"}', NULL, N'This regulatory guide 5.71 applies to operating nuclear reactors licensed in accordance with 10 CFR Part 50, and 10 CFR Part 52. Licensees and applicants should consider this guidance in preparing an application for a combined operating license under 10 CFR Part 52. Licensees and applicants bear the sole responsibility for assessing and managing the potential for adverse effects on safety, security, and emergency preparedness (SSEP) so as to provide high assurance that critical functions are adequately protected from cyber attacks.', N'NRC Regulatory Guide 5.71', '2022-09-20 15:26:46.847', 1)
