INSERT INTO [dbo].[ISE_ACTIONS] ([Mat_Question_Id], [Description], [Action_Items], [Regulatory_Citation], [Parent_Id]) VALUES (7808, N'The password controls are not sufficient to protect against current sophisticated threats. Strong passwords help mitigate against password based and other similar attacks.  Best practices call for passwords to be at least 14 characters and using passphrases. A passphrase is a series of words or other text that is generally longer than a traditional password. Access controls should also require inactive user accounts to be disabled after a period of inactivity.  Policies and standards should address unique strong passwords for users and disabling inactive accounts timely.', N'', N'13 C.F.R. Â§ 748.0 (b)(2)', 7687)
