INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact]) VALUES (2123, N'ACCESS-2i', N'[[Anomalous]] logical [[access]] attempts are [[monitoring|monitored]] as indicators of [[event|cybersecurity events]]', N'Monitoring is done on logical access attempts, and any anomalies detected (such as an attempted login with a user name that doesnt exist within the system) are tagged as requiring further review to determine whether they are indicators of cybersecurity events (rather than user error, for example).

