INSERT INTO [dbo].[MATURITY_QUESTIONS] ([Mat_Question_Id], [Question_Title], [Question_Text], [Supplemental_Info], [Category], [Sub_Category], [Maturity_Level_Id], [Sequence], [Maturity_Model_Id], [Parent_Question_Id], [Ranking], [Grouping_Id], [Examination_Approach], [Short_Name], [Mat_Question_Type], [Parent_Option_Id], [Supplemental_Fact]) VALUES (2127, N'ACCESS-3d', N'Physical [[access]] requirements are [[establish and maintain|established and maintained]] (for example, rules for who is allowed to access an [[assets|asset]], how access is granted, limits of allowed access)', N'It is the asset owners responsibility to ensure that requirements for protecting and sustaining assets are defined for assets under the owners control, including requirements for controlling physical access. For example, physical access requirements for vendor visits to a data center might require issuance of a temporary badge, escorted access, and a staff member monitoring the visitor''s activities.

