INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2109, CAST(31 AS Numeric(38, 0)), N'OMB m08-23.pdf', N'Securing the Federal Government''s Domain Name System Infrastructure (Submission of Draft Agency Plans Due by September 5, 2008)', N'OMB m08-23', 79227, N'OMB m08-23', NULL, NULL, N'OMB M-08-23 Securing DNS', NULL, NULL, N'This memorandum describes existing and new policies for deploying Domain Name System Security (DNSSEC) to all Federal information systems.  DNSSEC provides cryptographic protections to the Domain Name System (DNS) communication exchanges, thereby removing threats of DNS-based attacks and improving the overall integrity and authenticity of information processed over the Internet.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2111, CAST(31 AS Numeric(38, 0)), N'SP800-37-rev1-final.pdf', N'Guide for Applying the Risk Management Framework to Federal Information Systems', N'NIST SP800-37', 496647, N'NIST SP800-37', NULL, NULL, N'NIST SP800-37 Applying Risk Management', NULL, N'Revision 1', N'The purpose of this publication is to provide guidelines for applying the Risk Management Framework to federal information systems to include conducting the activities of security categorization, security control selection and implementation, security control assessment, information system authorization, and security control monitoring.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2112, CAST(31 AS Numeric(38, 0)), N'SP800-41-rev1.pdf', N'Guidelines on Firewalls and Firewall Policy', N'NIST SP800-41 R1', 429314, N'NIST SP800-41 R1', NULL, NULL, N'NIST SP800-41 Firewalls and Policy', NULL, N'Rev 1', N'This document provides an overview of firewall technologies and discusses their security capabilities and relative advantages and disadvantages in detail.  It provides examples of where firewalls can be placed within networks and the implications of deploying firewalls in particular locations.  The document also makes recommendations for establishing firewall policies and for selecting, configuring, testing, deploying, and managing firewall solutions.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2113, CAST(31 AS Numeric(38, 0)), N'SP800-46r1.pdf', N'Guide to Enterprise Telework and Remote Access Security', N'NIST SP800-46R1', 368708, N'NIST SP800-46R1', N'Security for Telecommuting and Broadband Communications', NULL, N'NIST SP800-46 Telework Guide', NULL, N'Revision 1', N'The purpose of this document is to assist organizations in mitigating the risks associated with the enterprise technologies used for telework, including remote access servers, telework client devices, and remote access communications.  It emphasizes the importance of securing sensitive information stored on telework devices and transmitted through remote access across external networks.  This document provides recommendations for creating telework-related policies and for selecting, implementing, and maintaining the necessary security controls for remote access servers and clients.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2114, CAST(31 AS Numeric(38, 0)), N'SP800-48r1.pdf', N'Guide to Securing Legacy IEEE 802.11 Wireless Networks', N'NIST SP800-48 R1', 1091076, N'NIST SP800-48 R1', N'Wireless Network Security for IEEE 802.11a/b/g and Bluetooth', NULL, N'NIST SP800-48 Legacy Wireless Networks', NULL, NULL, N'The purpose of this document is to provide guidance to organizations in securing their legacy Institute of Electrical and Electronics Engineers (IEEE) 802.11 wireless local area networks (WLAN) that cannot use IEEE 802.11i.  Details on securing WLANs capable of IEEE 802.11i can be found in NIST Special Publication (SP) 800-97.  Recommendations for securely using external WLANs, such as public wireless access points, are outside the scope of this document.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2116, CAST(31 AS Numeric(38, 0)), N'SP800-60_Vol2-Rev1.pdf', N'Volume II: Appendices to Guide for Mapping Types of Information and Information Systems to Security Categories', N'NIST SP800-60_vol2', 1607367, N'NIST SP800-60_vol2', N'SEK:  added doc num on Dec 1, 2010', NULL, N'NIST SP800-60 Vol 2 - Mapping Security Categories', NULL, N'Revision 1', N'This guideline has been developed to assist Federal government agencies to categorize information and information systems.  The guideline''s objective is to facilitate application of appropriate levels of information security according to a range of levels of impact or consequences that might result from the unauthorized disclosure, modification, or use of the information or information system.  Volume II contains appendices.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2117, CAST(31 AS Numeric(38, 0)), N'SP800-61rev1.pdf', N'Computer Security Incident Handling Guide (Rev 1)', N'NIST SP800-61', 1989015, N'NIST SP800-61', NULL, NULL, N'NIST SP800-61 Incident Handling Guide', NULL, N'Revision 1', N'This publication seeks to assist organizations in mitigating the risks from computer security incidents by providing practical guidelines on responding to incidents effectively and efficiently.  It includes guidelines on establishing an effective incident response program, but the primary focus of the document is detecting, analyzing, prioritizing, and handling incidents.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2118, CAST(31 AS Numeric(38, 0)), N'SP800-63-Rev1_Draft.pdf', N'Electronic Authentication Guideline_Draft', N'NIST SP800-63 R1', 945652, N'NIST SP800-63 R1', N'December 2008', NULL, N'NIST SP800-63 Electronic Authentication DRAFT', NULL, N'Draft', N'This document provides guidelines for implementing technology selection based on E-authentication technical guidance based on OMB memorandum M-04-04.  After completing a risk assessment and mapping the identified risks to the required assurance level, agencies can select appropriate technology that, at a minimum, meets the technical requirements for the required level of assurance.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2120, CAST(31 AS Numeric(38, 0)), N'SP800-70r1.pdf', N'National Checklist Program for IT Products - Guidelines for Checklist Users and Developers', N'NIST SP800-70 R1', 1699734, N'NIST SP800-70 R1', NULL, NULL, N'NIST SP800-70-1 Checklist Program Guidelines', NULL, N'Revision 1', N'This document describes security configuration checklists and their benefits, and explains how to use the NIST National Checklist Program (NCP) to find and retrieve checklists.  The document also describes the policies, procedures, and general requirements for participation in the NCP.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2123, CAST(31 AS Numeric(38, 0)), N'SP800-83.pdf', N'Guide to Malware Incident Prevention and Handling', N'NIST SP800-83', 3035967, N'NIST SP800-83', NULL, NULL, N'NIST SP800-83 Malware Incident', NULL, NULL, N'This publication provides recommendations for improving an organization''s malware incident prevention measures and for enhancing an organization''s existing incident response capability so that it is better prepared to handle widespread malware incidents.  The recommendations address several major forms of malware, including viruses, worms, Trojan horses, malicious mobile code, blended attacks, spyware tracking cookies, and attacker tools such as backdoors and rootkits.  The recommendations encompass various transmission mechanisms, including network services and removable media.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2127, CAST(31 AS Numeric(38, 0)), N'SP800-97.pdf', N'Establishing Wireless Robust Security Networks: A Guide to IEEE 802.11i (2-07)', N'NIST SP800-97', 4437324, N'NIST SP800-97', NULL, NULL, N'NIST SP800-97 Establishing Wireless', NULL, NULL, N'This publication seeks to assist organizations in understanding, selecting, and implementing technologies based on Institute of Electrical and Electronics Engineers (IEEE) 802.11i, part of the IEEE 802.11 family of wireless networking standards.  The document explains at length the security features and capabilities associated with IEEE 802.11i through its framework for Robust Security Networks (RSN), and provides extensive guidance on the planning and deployment of RSNs.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2128, CAST(31 AS Numeric(38, 0)), N'SP800-100-Mar07-2007.pdf', N'Information Security Handbook: A Guide for Managers', N'NIST SP800-100', 8558518, N'NIST SP800-100', NULL, NULL, N'NIST SP800-100 Guide for Managers', NULL, NULL, N'This Information Security Handbook provides a broad overview of information security program elements to assist managers in understanding how to establish and implement an information security program.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2129, CAST(31 AS Numeric(38, 0)), N'SP800-111.pdf', N'Guide to Storage Encryption Technologies for End User Devices', N'NIST SP800-111', 285729, N'NIST SP800-111', NULL, NULL, N'NIST SP800-111 Data Storage', NULL, NULL, N'The purpose of this document is to assist organizations in understanding storage encryption technologies for end user devices and in planning, implementing, and maintaining storage encryption solutions.  The types of end user devices addressed in this document are personal computers (desktops and laptops), consumer devices (e.g., personal digital assistants, smart phones), and removable storage media (e.g., USB flash drives, memory cards, external hard drives, writeable CDs and DVDs).', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2130, CAST(31 AS Numeric(38, 0)), N'SP800-113.pdf', N'Guide to SSL VPNs', N'NIST SP800-113', 1172534, N'NIST SP800-113', NULL, NULL, N'NIST SP800-113 Guide to SSL VPNs', NULL, NULL, N'This publication discusses the fundamental technologies and features of Secure Sockets Layer (SSL) virtual private networks (VPNs).  It describes SSL and how it fits within the context of layered network security, presents a phased approach to SSL VPN planning and implementation that can help in achieving successful SSL VPN deployments, and compares the SSL VPN technology with Internet Protocol Security (IPsec) VPNs and other VPN solutions.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2131, CAST(31 AS Numeric(38, 0)), N'SP800-121.pdf', N'Guide to Bluetooth Security', N'NIST SP800-121', 4076369, N'NIST SP800-121', NULL, NULL, N'NIST SP800-121 Bluetooth Security', NULL, NULL, N'The purpose of this document is to provide information to organizations on the security capabilities of Bluetooth and provide recommendations to organizations employing Bluetooth technologies on securing them effectively.  Bluetooth is an open standard for short-range radio frequency (RF) communication.', NULL, NULL, NULL)
