INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2140, CAST(31 AS Numeric(38, 0)), N'CIP-009-2.pdf', N'Cyber Security - Recovery Plans for Critical Cyber Assets', N'NERC Standard CIP-009-2', 19140, N'NERC CIP-009', NULL, N'Cyber Security - Recovery Plans for Critical Cyber Assets', N'NERC CIP-009-2 Recovery Plans', NULL, N'Rev. 2', N'North American Electric Reliability Corporation (NERC) Standard CIP-009 ensures that recovery plan(s) are put in place for Critical Cyber Assets and that these plans follow established business continuity and disaster recovery techniques and practices.', N'Standards Body', NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2141, CAST(31 AS Numeric(38, 0)), N'ISO 15408.pdf', N'Common Criteria - Part 2: Security functional components - Part 3: Security assurance components', N'ISO/IEC 15408', 4406576, N'ISO/IEC 15408', NULL, NULL, N'CC Part-2 Rev3 Security Functional Components', CAST(N'2009-07-01T00:00:00.000' AS DateTime), N'Version 3.1, Rev 3', N'The CC offers a set of well-understood security functional requirements that can be used to create trusted products reflecting the needs of the market.  These security functional requirements are presented as the current state of the art in requirements specification and evaluation.  Part 2 defines the required structure and content of security functional components for the purpose of security evaluation.  It includes a catalog of functional components that will meet the common security functionality requirements of many IT products.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2144, CAST(31 AS Numeric(38, 0)), N'SP800-73.pdf', N'Interfaces for Personal Identity Verification: Parts 1-4', N'NIST SP800-73', 973788, N'NIST SP800-73', NULL, NULL, N'NIST SP800-73 Personal Identity Verification', NULL, NULL, N'SP 800-73-3 contains the technical specifications to interface with smart cards to retrieve and use identity credentials.  The specifications are described in 4 parts and reflect the design goals of interoperability and PIV Card functions.  The goals are addressed by specifying the PIV data model, card edge interface, and application programming interface.', NULL, NULL, NULL)
