INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2600, CAST(31 AS Numeric(38, 0)), N'national_emergency_communications_plan.pdf', N'National Communications Emergency Plan 2008', N'NCSD 3-10', 4319054, N'NCSD 3-10', NULL, NULL, N'National Emergency Communications Plan', NULL, NULL, N'To strengthen emergency communications capabilities nationwide, this plan focuses on technology, coordination, governance, planning, usage, training and exercises at all levels of government. This approach recognizes that communications operability is a critical building block for interoperability; emergency response officials first must be able to establish communications within their own agency before they can interoperate with neighboring jurisdictions and other agencies.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2602, CAST(31 AS Numeric(38, 0)), N'NIST SP800-53 R4.pdf', N'NIST Special Publication 800-53 Revision 4 Security and Privacy Controls for Federal Information Systems and Organizations', N'NIST SP800-53 R4', 5709051, N'NIST SP800-53 R4', NULL, NULL, N'NIST SP800-53 Rev 4 - Security and Privacy Controls', NULL, NULL, N'This publication provides a catalog of security and privacy controls for federal information systems and organizations and a process for selecting controls to protect organizational operations (including mission, functions, image, and reputation), organizational assets, individuals, other organizations, and the Nation from a diverse set of threats including hostile cyber attacks, natural disasters, structural failures, and human errors (both intentional and unintentional). The security and privacy controls are customizable and implemented as part of an organization-wide process that manages information security and privacy risk. The controls address a diverse set of security and privacy requirements across the federal government and critical infrastructure, derived from legislation, Executive Orders, policies, directives, regulations, standards, and/or mission/business needs. The publication also describes how to develop specialized sets of controls, or overlays, tailored for specific types of missions/business functions, technologies, or environments of operation. Finally, the catalog of security controls addresses security from both a functionality perspective (the strength of security functions and mechanisms provided) and an assurance perspective (the measures of confidence in the implemented security capability). Addressing both security functionality and security assurance ensures that information technology products and the information systems built from those products using sound systems and security engineering principles are sufficiently trustworthy.', NULL, NULL, NULL)
