INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2744, CAST(31 AS Numeric(38, 0)), N'851001_2014.pdf', N'Department of Defense Instruction 8510.01', NULL, 666767, N'DODI-8510-01', NULL, NULL, N'DODI 8510.01 Risk Management Framework for DoD IT', NULL, NULL, N'This instruction implements References NIST 800-37, FISMA of 2002, CNSSI 1253, and NIST 800-53 by establishing the RMF for DoD IT (referred to in this instruction as the RMF), establishing associated cybersecurity policy, and assigning responsibilities for executing and maintaining the RMF. The RMF replaces the DoD Information Assurance Certification and Accreditation Process (DIACAP) and manages the life-cycle cybersecurity risk to DoD IT in accordance with NIST 800-53A, NIST 800-39, NIST 800-30, and DoD Directive 8115.01.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (2746, CAST(31 AS Numeric(38, 0)), N'nistir-7628_rev1_vol1_final.pdf', N'Guidelines for Smart Grid Cybersecurity', N'NISTIR 7628 Rev1', 9176458, N'NISTIR-7628R1', NULL, NULL, N'NIST IR-7628 Smart Grid', NULL, N'Rev 1', N'This three-volume report, Guidelines for Smart Grid Cyber Security, presents an analytical framework that organizations can use to develop effective cyber security strategies tailored to their particular combinations of Smart Grid-related characteristics, risks, and vulnerabilities. Organizations in the diverse community of Smart Grid stakeholders  from utilities to providers of energy management services to manufacturers of electric vehicles and charging stations  can use the methods and supporting information presented in this report as guidance for assessing risk and identifying and applying appropriate security requirements.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (3746, CAST(31 AS Numeric(38, 0)), N'NIST.SP.800-161.pdf', N'Supply Chain Risk Management Practices for Federal Information Systems and Organizations', NULL, 3950856, N'SP800-161', NULL, NULL, N'NIST SP800-161 Supply Chain Risk Management', NULL, NULL, N'Federal agencies are concerned about the risks associated with information and communications technology (ICT) products and services that may contain potentially malicious functionality, are counterfeit, or are vulnerable due to poor manufacturing and development practices within the ICT supply chain. These risks are associated with the federal agencies decreased visibility into, understanding of, and control over how the technology that they acquire is developed, integrated and deployed, as well as the processes, procedures, and practices used to assure the integrity, security, resilience, and quality of the products and services. This publication provides guidance to federal agencies on identifying, assessing, and mitigating ICT supply chain risks at all levels of their organizations. The publication integrates ICT supply chain risk management (SCRM) into federal agency risk management activities by applying a multitiered, SCRM-specific approach, including guidance on assessing supply chain risk and applying mitigation activities.', NULL, NULL, NULL)
