INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (3803, CAST(31 AS Numeric(38, 0)), N'SecureInternetAccess.pdf', N'Microsoft Guidance on Internet Security and Acceleration (ISA) Server 2004', N' Controlling Secure Internet Access using ISA Server 2004 ', 263680, N'Secure_Internet_Access', NULL, NULL, N'Secure_Internet_Access', CAST(N'2006-04-19T00:00:00.000' AS DateTime), N'Rev. 1', N'Microsoft Internet Security and Acceleration (ISA) Server 2004 provides granular control over how clients on your networks access the Internet. With the multi-networking functionality of ISA Server, you can apply this control to clients on any network whose gateway to the Internet is the ISA Server computer.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (3804, CAST(31 AS Numeric(38, 0)), N'skipnameresolution.pdf', N'Microsoft Guidance on Disabling Name Resolution on a Chained Downstream ISA Server Computer', N'Disabling Name Resolution on a Chained Downstream ISA Server Computer', 37888, N'Name_Resolution_Downstream', NULL, NULL, N'Name_Resolution_Downstream', CAST(N'2006-04-19T00:00:00.000' AS DateTime), N'Rev. 1', N'In a forward proxy scenario with chaining, a downstream Microsoft Internet Security and Acceleration (ISA) Server computer can be configured to send requests requiring DNS lookup directly to the upstream computer without performing name resolution by setting the SkipNameResolutionForAccessAndRoutingRules property of the FPCWebProxy object to True. When this property is set to True, the ISA Server Web proxy skips name resolution while checking access and routing rules. The Microsoft Visual Basic Scripting Edition (VBScript) code in SkipNameResolution.vbs sets this property to True and saves the new configuration setting to persistent storage.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (3805, CAST(31 AS Numeric(38, 0)), N'DigitalCertificates.pdf', N'Microsoft guidance on Digital Certificates', N'Digital Certificates for ISA Server 2004', 186880, N'Digital_Certificates', NULL, NULL, N'Digital_Certificates', CAST(N'2006-04-21T00:00:00.000' AS DateTime), N'Rev. 2', N'Digital certificates are the industry standard means of authentication and of providing encryption of sensitive data. A typical example of digital certificate use is in communication between a computer on the Internet and a Web server, in which credit card or bank account and password information may be transmitted. To prevent the reading of this information as it passes through the routers that connect the Internet, the HTTPS protocol, requiring digital certificates, is used.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (3806, CAST(31 AS Numeric(38, 0)), N'iOS8_Hardening_Guide.pdf', N'ACSC Hardening Guide IOS8', N' iOS Hardening Configuration Guide For iPod Touch, iPhone and iPad devices running iOS 8.3 or higher.', 5525294, N'IOS8_Hardening', NULL, NULL, N'IOS8_Hardening', CAST(N'2015-04-17T00:00:00.000' AS DateTime), N'Rev. 1', N'This guide provides instructions and techniques for Australian government agencies to harden the security of iOS 8 devices. Implementing the techniques and settings found in this document can affect system functionality, and may not be appropriate for every user or environment.  In these cases, agencies should seek approval for non-compliance from their accreditation authority to allow for the formal acceptance of the risks involved.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (3807, CAST(31 AS Numeric(38, 0)), N'PolicyRuleReport.pdf', N'Microsoft guidance on creating Policy Rule Reports', N'Creating Policy Rule Reports In Microsoft Internet Security and Acceleration (ISA) Server.', 118784, N'Policy_Rule_Report', NULL, NULL, N'Policy_Rule_Report', CAST(N'2004-10-23T00:00:00.000' AS DateTime), N'Rev. 1', N' In Microsoft Internet Security and Acceleration (ISA) Server, reports containing information about policy rules can be created by retrieving the properties of the objects that represent these rules. The Microsoft Visual Basic Scripting Edition (VBScript) code in PolicyRulesReport.vbs (script below) retrieves the collection of system policy rules and the collection of policy rules, and iterates through the collections, generating a report that includes the names of the rules with an indication of whether each rule is enabled. The script can be modified to generate reports that include other properties of the rules.', NULL, NULL, NULL)
INSERT [dbo].[GEN_FILE] ([Gen_File_Id], [File_Type_Id], [File_Name], [Title], [Name], [File_Size], [Doc_Num], [Comments], [Description], [Short_Name], [Publish_Date], [Doc_Version], [Summary], [Source_Type], [Data], [Is_Uploaded]) VALUES (3808, CAST(31 AS Numeric(38, 0)), N'PerfBestPractices.pdf', N'ISA Server 2004 Performance Best Practices', N'Performance Best Practices for the Microsoft Internet Security and Acceleration (ISA) Server.', 195072, N'PerfBest_Practices', NULL, NULL, N'PerfBest_Practices', CAST(N'2004-05-21T00:00:00.000' AS DateTime), N'Rev. 1', N' Microsoft Internet Security and Acceleration (ISA) Server 2004 provides controlled secure access between networks, and serves as a Web caching proxy providing fast Web response and offload capabilities. Its multi-layered architecture and advanced policy engine provide granular control of the balance between the level of security you need and the resources that are required. As an edge server connecting many networks, ISA Server handles large amounts of traffic compared to other servers in an organization. For this reason, it is built for high performance. This article provides guidelines for deploying ISA Server with best performance and adequate capacity.', NULL, NULL, NULL)
